                if bedIndex.text() == "R1" or bedIndex.text() == "R2" or bedIndex.text() == "R3" or bedIndex.text() == "R4":
                    self.tableWidget.setItem(currRow,bedColumn,QtWidgets.QTableWidgetItem(bedIndex.text()))     
